import React from "react";
import "./MovCtrl.css";
import Axios from 'axios';


function sendAPI(dir){
    const controls = { direction: dir };
    Axios.post('http://localhost:3001/mov_ctrl', controls);
}

class MovCtrl extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            direction: ''
        }

    }


    render() {

        return (

            <div class = "MovCtrl" id=""> 
                <button class="button forward" onClick={()=>sendAPI('UP')}>forward!</button> 
                <button class="button backward" onClick={()=>sendAPI('DOWN')}>backward!</button> 
                <button class="button left" onClick={()=>sendAPI('LEFT')}>left!</button> 
                <button class="button right" onClick={()=>sendAPI('RIGHT    ')}>right!</button> 

            </div>
        )

    }
}


export default MovCtrl;
